import express from 'express';
import { authToken } from '../../middleware/auth/auth.middle';
import { createSkill } from '../../service/implementators/skill/createSkill/showSkill.impl';
import { validate } from '../../middleware/globalValidator/globalValidator.middle';
import { validateSkillSet, validateUpdatedSkillSet } from '../../util/validators';
import { retrieveSkills } from '../../service/implementators/skill/fetchSkills/fetchSkills.impl';
import { retrieveSkill } from '../../service/implementators/skill/fetchSkill/fetchSkill.impl';
import { editAndUpdateSkill } from '../../service/implementators/skill/updateSkill/updateSkill.impl';
import { eradicateSkill } from '../../service/implementators/skill/deleteSkill/removeSkill.impl';
const router = express.Router();
router.post('/create-skill', authToken, validate(validateSkillSet), createSkill);
router.get('/fetch-skills', authToken, retrieveSkills);
router.get('/fetch-skill/:id', authToken, retrieveSkill);
router.put('/update-skill/:id', authToken, validate(validateUpdatedSkillSet), editAndUpdateSkill);
router.delete('/delete-skill/:id', authToken, eradicateSkill);
export default router;
